mapper.select:
SELECT TRPR_ID, TRPR_DEGR
	FROM TRPR_INFO
WHERE TRPR_ID=#{trpr_id} AND TRPR_DEGR=#{trpr_degr}

SELECT TRAINST_CST_ID
	FROM TRAINST_INFO
WHERE TRAINST_CST_ID=#{trainst_cst_id}

SELECT INO_NM
	FROM TRAINST_INFO
WHERE TRAINST_CST_ID=#{trainst_cst_id}

mapper.update:
UPDATE TRPR_INFO
SET EQMN_INFO_LIST=#{eqmn_info_list}, FACIL_INFO_LIST=#{facil_info_list}
WHERE TRPR_ID=#{trpr_id} AND TRPR_DEGR=#{trpr_degr}
